mapper.select:
select uf.id, uf.download, bs.name, og.groupname, boxtype.type, uf.file_version fileVersion, uf.enforce, uf.update_time updateTime, uf.status, uf.description, uf.mappingid from updatefile uf LEFT JOIN boxtype on boxtype.id = uf.modelid LEFT JOIN business bs ON uf.businessid = bs.id LEFT JOIN otagroup og on og.id = uf.groupid
businessid=#{customerid,jdbcType=INTEGER}
and modelid=#{modelid,jdbcType=VARCHAR}
